import {Box, Avatar, makeStyles} from '@material-ui/core';
import {AccountContext} from '../../context/AccountProvider'
import { useContext } from 'react';
import ChatIcon from '@mui/icons-material/Chat';
import HeaderMenu from './HeaderMenu';

const useStyles = makeStyles ({
    header: {
        height: '20 auto', // '25 auto' || 59
        width: '100%',
        background: '#f0f2f5',
        padding: '10px 16px',
        display: 'flex',
        alignItems: 'center'
    },
    avatar: {
        height: 37,
        width: 37,
        borderRadius: '50%'
    },
    icons: {
        marginLeft: 'auto',
        '& > *': {
            marginLeft: 14,
            color:'#54656f',
            padding: 1
        },
        '& :first-child': {
            fontSize: 24,
            marginRight: 8,
            marginTop: 3
        }
    }
})

const Header = () => {
    const classes = useStyles();
    const {account} = useContext(AccountContext);
    // console.log(account);

    return (
    //   <p>Hello from Header</p>
      <Box className={classes.header}>
          <Avatar src={account.imageUrl} title="Perfil" alt="Perfil" style={{cursor: 'pointer'}} className={classes.avatar}/> {/*Pode ser uma <img> tamb√©m!*/}
          <Box className={classes.icons}>
              <ChatIcon/>
              <HeaderMenu/>
          </Box>
      </Box>
    );
}

export default Header;
