import {Box, Avatar, makeStyles} from '@material-ui/core';
import {AccountContext} from '../../context/AccountProvider'
import { useContext } from 'react';
import ChatIcon from '@mui/icons-material/Chat';
import HeaderMenu from './HeaderMenu';

const useStyles = makeStyles ({
    header: {
        height: '20 auto', // '25 auto' || 59
        width: '100%',
        background: '#f0f2f5',
        padding: '10px 16px',
        display: 'flex',
        alignItems: 'center'
    },
    avatar: {
        height: 37,
        width: 37,
        borderRadius: '50%'
    },
    icons: {
        marginLeft: 'auto',
        '& > *': {
            marginLeft: 14,
            color:'#54656f',
            padding: 1
        },
        '& :first-child': {
            fontSize: 24,
            marginRight: 8,
            marginTop: 3
        }
    }
})

const Header = () => {
    const classes = useStyles();
    const {account} = useContext(AccountContext);
    // console.log(account);

    return (
    //   <p>Hello from Header</p>
      <Box className={classes.header}>
          <Avatar src={account.imageUrl} title="Perfil" alt="Perfil" style={{cursor: 'pointer'}} className={classes.avatar}/> {/*Pode ser uma <img> também!*/}
          <Box className={classes.icons}>
              <svg version="1.1" x="0" y="0" viewBox="0 0 24 24" width="24" height="24" style={{cursor: 'pointer'}} title="Opções"><path fill="currentColor" d="M12 20.664a9.163 9.163 0 0 1-6.521-2.702.977.977 0 0 1 1.381-1.381 7.269 7.269 0 0 0 10.024.244.977.977 0 0 1 1.313 1.445A9.192 9.192 0 0 1 12 20.664zm7.965-6.112a.977.977 0 0 1-.944-1.229 7.26 7.26 0 0 0-4.8-8.804.977.977 0 0 1 .594-1.86 9.212 9.212 0 0 1 6.092 11.169.976.976 0 0 1-.942.724zm-16.025-.39a.977.977 0 0 1-.953-.769 9.21 9.21 0 0 1 6.626-10.86.975.975 0 1 1 .52 1.882l-.015.004a7.259 7.259 0 0 0-5.223 8.558.978.978 0 0 1-.955 1.185z"></path></svg>
              <ChatIcon style={{cursor: 'pointer'}}/>
              <HeaderMenu style={{cursor: 'pointer'}}/>
          </Box>
      </Box>
    );
}

export default Header;
