import {Box, Avatar, makeStyles, Typography} from '@material-ui/core';
import {AccountContext} from '../../context/AccountProvider';
import {useContext} from 'react';

const useStyles = makeStyles({
  imageContainer: {
    display: 'flex',
    justifyContent: 'center'
    // alignItems: 'center'
  },
   avatar: {
     width: '48%',
     height: '48%',
     cursor: 'pointer',
     marginTop: 28,
     borderRadius: '50%',
     '&:hover': {
        opacity: '70%',
        filter: 'grayscale(1)'
      }
   },
   titleName: {
     color: '#164194',
     fontSize: 'xx-medium'
   }
})

const Profile = () => {
  const {account} = useContext(AccountContext);
  const classes = useStyles();

    return (
        <>
          <Box className={classes.imageContainer}>
            <Avatar src={account.imageUrl} title={account.name} alt='Profile-Pic' className={classes.avatar}/>
          </Box>

          <Box>
            <Typography className={classes.titleName}>Seu nome</Typography>
            <Typography>{account.name}</Typography>
          </Box>
          
          <Box>
          </Box>
          
          <Box>
          </Box>
        </>
    )
}

export default Profile;
