import {Box, makeStyles, InputBase, IconButton} from '@material-ui/core';

const useStyles = makeStyles(theme => ({
    footer: {
        height: 53,
        background: '#f0f2f5',
        width: '100%',
        display: 'flex',
        alignItems: 'center',
        padding: 0,
        '& > *': {
            margin: 5,
            color: '#919191'
        }
    },
    input: {
        backgroundColor: '#fff',
        borderRadius: 5.7,
        height: 35
    },
    gravar: {
        backgroundColor: 'transparent',
        outline: 'none',
        outlineStyle: 'none',
        outlineWidth: 'none',
        width: 37,
        height: 47,
        border: 'transparent',
        cursor: 'pointer',
        marginRight: 12
    },
    inputRoot: {
        color: '#000',
        width: '98.7%'
    },
    inputInput: {
        fontSize: 14,
        paddingLeft: 14,
        height: 27
    },
    typeBox: {
        backgroundColor: '#fff',
        borderRadius: 7,
        width: '90%',
        marginBottom: 6
    }
}))

const Footer = () => {
    const classes = useStyles();
    return (
        <Box className={classes.footer}>
            <IconButton title="Anexar"><svg viewBox="0 0 24 24" width="24" height="24" class=""><path fill="#54656f" d="M1.816 15.556v.002c0 1.502.584 2.912 1.646 3.972s2.472 1.647 3.974 1.647a5.58 5.58 0 0 0 3.972-1.645l9.547-9.548c.769-.768 1.147-1.767 1.058-2.817-.079-.968-.548-1.927-1.319-2.698-1.594-1.592-4.068-1.711-5.517-.262l-7.916 7.915c-.881.881-.792 2.25.214 3.261.959.958 2.423 1.053 3.263.215l5.511-5.512c.28-.28.267-.722.053-.936l-.244-.244c-.191-.191-.567-.349-.957.04l-5.506 5.506c-.18.18-.635.127-.976-.214-.098-.097-.576-.613-.213-.973l7.915-7.917c.818-.817 2.267-.699 3.23.262.5.501.802 1.1.849 1.685.051.573-.156 1.111-.589 1.543l-9.547 9.549a3.97 3.97 0 0 1-2.829 1.171 3.975 3.975 0 0 1-2.83-1.173 3.973 3.973 0 0 1-1.172-2.828c0-1.071.415-2.076 1.172-2.83l7.209-7.211c.157-.157.264-.579.028-.814L11.5 4.36a.572.572 0 0 0-.834.018l-7.205 7.207a5.577 5.577 0 0 0-1.645 3.971z"></path></svg></IconButton>
            <Box className={classes.typeBox}>
                <InputBase
                  placeholder='Mensagem'
                  className={classes.input}
                  title='Mensagem'
                  color='#54656f'
                  classes={{
                      root: classes.inputRoot,
                      input: classes.inputInput,
                  }}
                  inputProps={{ 'aria-label': 'search' }}
                />
            </Box>
            <button title="Gravar" className={classes.gravar}><svg viewBox="0 0 24 24" width="24" height="24"><path fill="#54656f" d="M11.999 14.942c2.001 0 3.531-1.53 3.531-3.531V4.35c0-2.001-1.53-3.531-3.531-3.531S8.469 2.35 8.469 4.35v7.061c0 2.001 1.53 3.531 3.53 3.531zm6.238-3.53c0 3.531-2.942 6.002-6.237 6.002s-6.237-2.471-6.237-6.002H3.761c0 4.001 3.178 7.297 7.061 7.885v3.884h2.354v-3.884c3.884-.588 7.061-3.884 7.061-7.885h-2z"></path></svg></button>
        </Box>
    )
}

export default Footer;
